import { useState } from 'react'
import Card from '../UI/Card'
import classes from './Product.module.css'
import ProductItem from './ProductItem/ProductItem'
// import ProductDetail from './ProductDetail'

const DUMMY_PRODUCTS = [
  {
    id: 'm1',
    name: 'Men Shirt',
    brand: 'Gucci',
    sku: 'GUCCIMEN',
    price: 220.99,
    quantity: 200
  },
  {
    id: 'm2',
    name: 'Ladies Dress',
    brand: 'Versace',
    sku: 'GUCCIMEN',
    price: 100.99,
    quantity: 200
  },
  {
    id: 'm3',
    name: 'Men Shirt',
    brand: 'Prada',
    sku: 'GUCCIMEN',
    price: 80.0,
    quantity: 200
  },
  {
    id: 'm4',
    name: 'Chino Trousers',
    brand: 'Gucci',
    sku: 'GUCCIMEN',
    price: 180.99,
    quantity: 200
  }
]

const Product = props => {
  const [modalIsVisible, setModalVisible] = useState(false)

  const showProdDetailsHandler = () => {
    setModalVisible(true)
  }

  const hideProdDetailsHandler = () => {
    setModalVisible(false)
  }

  const productList = DUMMY_PRODUCTS.map(product => (
    <ProductItem
      key={product.id}
      name={product.name}
      brand={product.brand}
      price={product.price}
      handleShowProdDetails={showProdDetailsHandler}
    />
  ))

  return (
    <section className={classes.products}>
      {/* {modalIsVisible && <ProductDetail handleHideModal={hideProdDetailsHandler} />} */}
      <Card>
        <ul>{productList}</ul>
      </Card>
    </section>
  )
}

export default Product
