import classes from './ProductItem.module.css'
import { Fragment, useState } from 'react'
import ProductDetail from '../ProductDetail'

const ProductItem = props => {
  const [modalIsVisible, setModalVisible] = useState(false)

  const showProdDetailsHandler = () => {
    setModalVisible(true)
  }

  const hideProdDetailsHandler = () => {
    setModalVisible(false)
  }

  const price = `$${props.price.toFixed(2)}`
  return (
    <Fragment>
      {modalIsVisible && <ProductDetail handleHideModal={hideProdDetailsHandler} />}

      <li className={classes.product} onClick={props.handleShowProdDetails}>
        <div>
          <h3>{props.name}</h3>
          <div className={classes.brand}>{props.brand}</div>
          <div className={classes.price}>{price}</div>
        </div>
        <div></div>
      </li>
    </Fragment>
  )
}

export default ProductItem
