import { useState } from 'react'
import Card from '../UI/Card'
import classes from './Product.module.css'
import ProductItem from './ProductItem/ProductItem'

const DUMMY_PRODUCTS = [
  {
    id: 'm1',
    name: 'Men Shirt',
    brand: 'Gucci',
    sku: 'GUCCIMEN',
    price: 220.99,
    quantity: 200
  },
  {
    id: 'm2',
    name: 'Ladies Dress',
    brand: 'Versace',
    sku: 'GUCCIMEN',
    price: 100.99,
    quantity: 200
  },
  {
    id: 'm3',
    name: 'Men Shirt',
    brand: 'Prada',
    sku: 'GUCCIMEN',
    price: 80.0,
    quantity: 200
  },
  {
    id: 'm4',
    name: 'Chino Trousers',
    brand: 'Gucci',
    sku: 'GUCCIMEN',
    price: 180.99,
    quantity: 200
  }
]

const Product = props => {
  const [productList, setProductList] = useState([])

  function fetchProductsHandler () {
    fetch('https://jsonplaceholder.typicode.com/posts/')
      .then(response => {
        return response.json()
      })
      .then(data => {
        console.log(data)
        setProductList()
      })
  }
  fetchProductsHandler()
  const products = DUMMY_PRODUCTS.map(product => (
    <ProductItem
      key={product.id}
      id={product.id}
      sku={product.sku}
      name={product.name}
      brand={product.brand}
      price={product.price}
      quantity={product.quantity}
    />
  ))

  return (
    <section className={classes.products}>
      <Card>
        <ul>{products}</ul>
      </Card>
    </section>
  )
}

export default Product
